mapper.select:
select username, userid, email
from ssafy_member
where userid = #{userid} and userpwd = #{userpwd}

select username, userid, email, joindate
from ssafy_member
where userid = #{userid}

mapper.insert:
insert into ssafy_member(
	userid, username, userpwd, email
) values (
	#{userid}, #{username}, #{userpwd}, #{email}
)
